import React, { useState } from "react";
import { Button } from "./components/ui/button";
import { Input } from "./components/ui/input";
import { Label } from "./components/ui/label";
import { Textarea } from "./components/ui/textarea";
import axios from 'axios';
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "./components/ui/card";


// Define the Hazard type 
type Hazard = { 
  name: string;
  type: string;
  description: string;
  riskLevel: string;
  targetType: string;
  path: string;
};

// Placeholder function to interact with LLAMA
const generateMitigations = async (hazard: Hazard) => {
  try {
    const response = await axios.post('http://localhost:5000/generate', {
      prompt: `Detected hazard: ${hazard.message}. How can this be mitigated?`
    });
    return response.data.response;
  } catch (error) {
    console.error('Error generating mitigations:', error);
    return 'Error generating mitigation.';
  }
};


const HazardDetection = () => {
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [jsonData, setJsonData] = useState(null);
  const [hazards, setHazards] = useState<Hazard[]>([]);
  const [safetyText, setSafetyText] = useState("");
  const [mitigations, setMitigations] = useState<string[]>([]);
  const [showJsonContext, setShowJsonContext] = useState(false);

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0]; // Optional chaining in case no file is selected
    if (file) {
      setSelectedFile(file);
      const reader = new FileReader();
      reader.onload = (loadEvent) => {
        try {
          const json = JSON.parse(loadEvent.target?.result as string); // Cast the result to a string
          setJsonData(json);
          console.log("Parsed JSON Data:", json);
        } catch (error) {
          console.error("Error parsing JSON:", error);
        }
      };
      reader.readAsText(file);
    }
  };


  const handleDragOver = (event: React.DragEvent<HTMLDivElement>) => {
    event.preventDefault();
  };


  const handleDrop = (event: React.DragEvent<HTMLDivElement>) => {
    event.preventDefault();
    const file = event.dataTransfer.files[0]; // Access the first file
    if (file.type === "application/json") {
      setSelectedFile(file);
      const reader = new FileReader();
      reader.onload = (loadEvent) => {
        try {
          const json = JSON.parse(loadEvent.target?.result as string); // Cast the result to a string
          setJsonData(json);
          console.log("Parsed JSON Data:", json);
        } catch (error) {
          console.error("Error parsing JSON:", error);
        }
      };
      reader.readAsText(file);
    } else {
      console.error("Only JSON files are accepted");
    }
  };

  const detectHazards = (data: any) => {
    const hazards: Hazard[] = [];
    const uniqueHazards = new Set(); // Track hazard types or unique identifiers
    const stack: { obj: any; path: string }[] = [{ obj: data, path: "" }]; // Stack with objects and their paths

    while (stack.length > 0) {
      const { obj, path } = stack.pop()!; // Get the next item and its path

      if (typeof obj !== "object" || obj === null) continue;

      // Traverse keys of the current object
      Object.keys(obj).forEach((key) => {
        const newPath = path ? `${path}.${key}` : key; // Update the path

        if (typeof obj[key] === "object" && obj[key] !== null) {
          stack.push({ obj: obj[key], path: newPath }); // Add child objects with updated path
        } else {
          // Process Relation keys first (includes Hazards)
          if (key.toLowerCase() === "relations" && Array.isArray(obj[key])) {
            obj[key].forEach((relation: any, index: number) => {
              if (relation.Hazards && Array.isArray(relation.Hazards)) {
                relation.Hazards.forEach((hazard: any, hazardIndex: number) => {
                  // Check for unique hazards and push them
                  const uniqueIdentifier = `${hazard.Type || ""}-${hazard.Message || ""}`;
                  if (!uniqueHazards.has(uniqueIdentifier)) {
                    hazards.push({
                      name: hazard.Name || "",
                      type: hazard.Type || "",
                      description: hazard.Description || "",
                      riskLevel: hazard.RiskLevel || "",
                      targetType: hazard.TargetType || "",
                      path: `${newPath}.Relations[${index}].Hazards[${hazardIndex}]`, // Path with indices
                    });
                    uniqueHazards.add(uniqueIdentifier);
                  }
                });
              }
            });
          }

          // Handle individual hazard properties within each hazard if they are in the Properties array
          if (key.toLowerCase() === "properties" && Array.isArray(obj[key])) {
            const hazard: Hazard = {
              name: "",
              description: "",
              type: "",
              riskLevel: "",
              targetType: "",
              path, // Use the current path
            };

            obj[key].forEach((property: any) => {
              const [key, value] = property;

              if (key === "Name" && value.LocalValue) {
                hazard.name = value.LocalValue;
              }
              if (key === "Description" && value.LocalValue) {
                hazard.description = value.LocalValue;
              }
              if (key === "Type" && value.LocalValue) {
                hazard.type = value.LocalValue;
              }
              if (key === "Risk" && value.LocalValue) {
                hazard.riskLevel = value.LocalValue;
              }
              if (key === "TargetType" && value.LocalValue) {
                hazard.targetType = value.LocalValue;
              }
            });

            if (hazard.type) {
              const uniqueIdentifier = `${hazard.type}-${hazard.name || ""}-${hazard.description || ""}`;
              if (!uniqueHazards.has(uniqueIdentifier)) {
                hazards.push(hazard);
                uniqueHazards.add(uniqueIdentifier);
              }
            }
          }
        }
      });
    }

    console.log("Detected Hazards:", hazards);
    return hazards;
  };  


  const handleDetectHazards = () => {
    if (jsonData) {
      const detectedHazards = detectHazards(jsonData); // Pass the updated configuration
      setHazards(detectedHazards);

      const safetyText = detectedHazards
        .map(
          (hazard, index) =>
            `${index + 1} - ${hazard.type}:\n  - ${hazard.message}`
        )
        .join("\n\n");
      setSafetyText(safetyText);
    }
  };

  const handleGenerateMitigations = async () => {
    const mitigationsList = await Promise.all(
      hazards.map(async (hazard) => {
        const mitigation = await generateMitigations(hazard);
        return `${hazard.type}: ${mitigation}`;
      })
    );
    setMitigations(mitigationsList);
  };

  return (
    <div className="max-w-3xl mx-auto p-4">
      <Card>
        <CardHeader>
          <CardTitle>JSON Data Analysis for Hazard Detection</CardTitle>
          <CardDescription>
            Upload a JSON file to analyze and generate safety texts for
            automatic hazard detection.
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div
            className="border-2 border-dashed rounded-xl p-4"
            onDragOver={handleDragOver}
            onDrop={handleDrop}
          >
            <Label>Drag and drop a JSON file or upload from device</Label>
            <Input type="file" onChange={handleFileChange} accept=".json" />
          </div>
          {selectedFile && (
            <div className="mt-4">
              <Button onClick={() => setShowJsonContext(!showJsonContext)}>
                {showJsonContext ? "Hide JSON Context" : "View JSON Context"}
              </Button>
              {showJsonContext && (
                <Textarea
                  value={JSON.stringify(jsonData, null, 2)}
                  readOnly
                  className="mt-4"
                />
              )}
            </div>
          )}
          {jsonData && (
            <div className="mt-4">
              <Button onClick={handleDetectHazards}>Detect Hazards</Button>
              {safetyText && (
                <div className="mt-4">
                  <h2 className="text-lg font-bold">Detected Hazards:</h2>
                  <Textarea value={safetyText} readOnly className="mt-4" />
                </div>
              )}
            </div>
          )}
          {hazards.length > 0 && (
            <div className="mt-4">
              <Button onClick={handleGenerateMitigations}>
                Generate Mitigations
              </Button>
              {mitigations.length > 0 && (
                <div className="mt-4">
                  <h2 className="text-lg font-bold">Generated Mitigations:</h2>
                  <Textarea
                    value={mitigations.join("\n\n")}
                    readOnly
                    className="mt-4"
                  />
                </div>
              )}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

export default HazardDetection;
