import React, { useState } from "react";
import { Button } from "/components/ui/button";
import { Input } from "/components/ui/input";
import { Label } from "/components/ui/label";
import { Textarea } from "/components/ui/textarea";
import {
  Card,
  CardContent,
  CardDescription,
  CardHeader,
  CardTitle,
} from "/components/ui/card";

// Placeholder function to interact with LLAMA
const generateMitigations = async (hazard) => {
  // Replace this with actual API call to LLAMA
  return `Mitigation for ${hazard.type}: Follow standard safety protocols and use appropriate PPE.`;
};

const HazardDetection = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [jsonData, setJsonData] = useState(null);
  const [hazards, setHazards] = useState([]);
  const [safetyText, setSafetyText] = useState("");
  const [mitigations, setMitigations] = useState([]);
  const [showJsonContext, setShowJsonContext] = useState(false);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
    const reader = new FileReader();
    reader.onload = (event) => {
      try {
        const json = JSON.parse(event.target.result);
        setJsonData(json);
        console.log("Parsed JSON Data:", json);
      } catch (error) {
        console.error("Error parsing JSON:", error);
      }
    };
    reader.readAsText(event.target.files[0]);
  };

  const handleDragOver = (event) => {
    event.preventDefault();
  };

  const handleDrop = (event) => {
    event.preventDefault();
    const file = event.dataTransfer.files[0];
    if (file.type === "application/json") {
      setSelectedFile(file);
      const reader = new FileReader();
      reader.onload = (event) => {
        try {
          const json = JSON.parse(event.target.result);
          setJsonData(json);
          console.log("Parsed JSON Data:", json);
        } catch (error) {
          console.error("Error parsing JSON:", error);
        }
      };
      reader.readAsText(file);
    } else {
      console.error("Only JSON files are accepted");
    }
  };

  const detectHazards = (data) => {
    const hazards = [];
    const recursiveDetect = (obj, path = "") => {
      Object.keys(obj).forEach((key) => {
        const newPath = path ? `${path}.${key}` : key;
        console.log(`Checking ${newPath}`); // Debugging line
        if (typeof obj[key] === "object" && obj[key] !== null) {
          recursiveDetect(obj[key], newPath);
        } else {
          if (
            obj["Type"] &&
            obj["Type"].toLowerCase() === "hazard" &&
            key.toLowerCase() === "name"
          ) {
            hazards.push({
              type: obj[key],
              message: obj["Message"],
              riskLevel: "Unknown",
              path: newPath, // Optional: Add path for easier debugging
            });
            console.log(`Hazard detected: ${obj[key]} - ${obj["Message"]}`);
          }
        }
      });
    };
    recursiveDetect(data);
    console.log("Detected Hazards:", hazards);
    return hazards;
  };

  const handleDetectHazards = () => {
    if (jsonData) {
      const detectedHazards = detectHazards(jsonData);
      setHazards(detectedHazards);
      const safetyText = detectedHazards
        .map(
          (hazard, index) =>
            `${index + 1} - ${hazard.type}:\n  - ${hazard.message}`
        )
        .join("\n\n");
      setSafetyText(safetyText);
      console.log("Detected Hazards:", safetyText);
    }
  };

  const handleGenerateMitigations = async () => {
    const mitigationsList = await Promise.all(
      hazards.map(async (hazard) => {
        const mitigation = await generateMitigations(hazard);
        return `${hazard.type}: ${mitigation}`;
      })
    );
    setMitigations(mitigationsList);
  };

  return (
    <div className="max-w-3xl mx-auto p-4">
      <Card>
        <CardHeader>
          <CardTitle>JSON Data Analysis for Hazard Detection</CardTitle>
          <CardDescription>
            Upload a JSON file to analyze and generate safety texts for
            automatic hazard detection.
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div
            className="border-2 border-dashed rounded-xl p-4"
            onDragOver={handleDragOver}
            onDrop={handleDrop}
          >
            <Label>Drag and drop a JSON file or upload from device</Label>
            <Input type="file" onChange={handleFileChange} accept=".json" />
          </div>
          {selectedFile && (
            <div className="mt-4">
              <Button onClick={() => setShowJsonContext(!showJsonContext)}>
                {showJsonContext ? "Hide JSON Context" : "View JSON Context"}
              </Button>
              {showJsonContext && (
                <Textarea
                  value={JSON.stringify(jsonData, null, 2)}
                  readOnly
                  className="mt-4"
                />
              )}
            </div>
          )}
          {jsonData && (
            <div className="mt-4">
              <Button onClick={handleDetectHazards}>Detect Hazards</Button>
              {safetyText && (
                <div className="mt-4">
                  <h2 className="text-lg font-bold">Detected Hazards:</h2>
                  <Textarea value={safetyText} readOnly className="mt-4" />
                </div>
              )}
            </div>
          )}
          {hazards.length > 0 && (
            <div className="mt-4">
              <Button onClick={handleGenerateMitigations}>
                Generate Mitigations
              </Button>
              {mitigations.length > 0 && (
                <div className="mt-4">
                  <h2 className="text-lg font-bold">Generated Mitigations:</h2>
                  <Textarea
                    value={mitigations.join("\n\n")}
                    readOnly
                    className="mt-4"
                  />
                </div>
              )}
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
};

export default HazardDetection;
